import styled from "styled-components";

export const HeaderBox = styled.div`
  @media (max-width: 700px) {
    h4 {
      margin-left: 0%;
    }
    &.active {
      padding: 2rem 1rem 18rem 1rem;
    }
  }
  @media (min-width: 700px) {
    h4 {
      margin-left: 0%;
    }
    &.active {
      padding: 2rem 1rem 18rem 1rem;
    }
  }

`;

export const Logo = styled.img`
  // width:140px;
  height: 26px;
  cursor: pointer;
  border-radius: 100%;
`;
