import styled from "styled-components";

export const Card = styled.div`
  @media (max-width: 700px) {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 12.182px;
    gap: 12.18px;

    width: 280px;
    height: 48.73px;
    left: 12.18px;
    top: 75.12px;

    background-color: var(--grey-4);
    color: var(--grey-0);
    &:hover {
      background-color: var(--grey-2);
    }
    font: var(--tech-title-700);
    border: none;
    border-radius: 4px;

    button {
      margin: 0px;
    }
  }
  @media (mi-width: 700px) {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  padding: 12.182px;
  gap: 12.18px;

  width: 742px;
  height: 48.73px;
  left: 12.18px;
  top: 75.12px;

  background-color: var(--grey-4);
  color: var(--grey-0);
  &:hover {
    background-color: var(--grey-2);
  }
  font: var(--tech-title-700);
  border: none;
  border-radius: 4px;

  button {
    margin: 0px;
  }
`;
