import * as React from 'react';
import { Input } from "../input/input";
import { Alert, Modal, Snackbar, SnackbarOrigin } from "@mui/material";
import { ReactElement, useState } from "react";
import { ModalTechsProps, State } from "../../interface";
import { Button } from "../button/button";
import { TechForm, TechFormHeader } from "./style";
import CloseIcon from "@mui/icons-material/Close";

export const TechModal = ({
  open,
  handleModal,
  type,
  techData, 
}: ModalTechsProps): ReactElement => {
  const [state, setState] = React.useState<State>({
    isOpenAlert: false,
    vertical: "top",
    horizontal: "center",
  });
  const { vertical, horizontal, isOpenAlert } = state;

  const handleClick = (newState: SnackbarOrigin) => () => {
    setState({ isOpenAlert: true, ...newState });
  };

  const handleClose = () => {
    setState({ ...state, isOpenAlert: false });
  };
  return (
    <>
      <Snackbar
        anchorOrigin={{ vertical, horizontal }}
        open={isOpenAlert}
        onClose={handleClose}
        message="I love snacks"
        key={vertical + horizontal}
      />

      <Modal
        open={open}
        onClose={handleModal}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        {type === "register" ? (
          <TechForm>
            <TechFormHeader>
              <p>cadastre sua tecnologia</p>
              <CloseIcon
                onClick={() => handleModal({ typeOfForm: "register" })}
              />
            </TechFormHeader>
            <Input
              label="Nome da Tecnologia"
              type="text"
              placeholder="Typescript"
            ></Input>
            <Input label="Status" type="text" placeholder="junior"></Input>
            <Button
              type="button"
              variant="default"
              variant_hover="default_hover"
              size="large"
              onClick={handleClick({ vertical: "top", horizontal: "center" })}
            >
              {"Cadastrar Tecnologia"}
            </Button>
          </TechForm>
        ) : (
          <TechForm>
            <TechFormHeader>
              <p>Editar Sua Tecnologia</p>
              <CloseIcon onClick={() => handleModal({ typeOfForm: "edit" })} />
            </TechFormHeader>
            <Input
              label="Nome da Tecnologia"
              type="text"
              value={techData?.name}
            ></Input>
            <Input label="Status" type="text" value={techData?.status}></Input>
            <Button
              type="button"
              variant="default"
              variant_hover="default_hover"
              size="large"
            >
              {"Editar Tecnologia"}
            </Button>
          </TechForm>
        )}
      </Modal>
    </>
  );
};
