import styled from "styled-components";
// import { width } from "styled-system";

export const Card = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    padding: 12.182px;
    gap: 12.18px;

    position: absolute;
    width: 742px;
    height: 48.73px;
    left: 12.18px;
    top: 75.12px;

    background-color:var(--grey-4);
    color:var(--grey-0);
    // width: 752px;
    // height: 50px; 
    &:hover{
    background-color:var(--grey-2);
    }      
    font:var(--tech-title-700);
    // border: none;
    border-radius: 4px;

    button{
        margin:0px;
    }
`;
