import { createGlobalStyle, } from "styled-components";


export const GlobalStyle = createGlobalStyle`
    *{
        //Font-Family import
        @import url('https://fonts.googleapis.com/css2?family=Inter&family=Lexend&display=swap');
    }
    :root {

        //Band
        --brand-1: #4529E6;
        --brand-2: #5126EA;
        --brand-3: #B0A6F0;
        --brand-4: #EDEAFD;

        //Grey Scale
        --grey-0: #0B0D0D;
        --grey-1: #212529;
        --grey-2: #495057;
        --grey-3: #868E96;
        --grey-4: #ADB5BD;
        --grey-5: #CED4DA;
        --grey-6: #DEE2E6;
        --grey-7: #E9ECEF;
        --grey-8: #F1F3F5;
        --grey-9: #F8F9FA;
        --grey-10: #FDFDFD;
        --white-fixed: #FFFFFF;

        //FeedBack
        --alert-1: #CD2B31;
        --alert-2: #FDD8D8;
        --alert-3: #FFE5E5;

        --sucess-1: #18794E;
        --sucess-2: #CCEBD7;
        --sucess-3: #DDF3E4;

        //Colors Random Profile
        --random-1: #E34D8C;
        --random-2: #C04277;
        --random-3: #7D2A4D;
        --random-4: #7000FF;
        --random-5: #6200E3;
        --random-6: #36007D;
        --random-7: #349974;
        --random-8: #2A7D5F;
        --random-9: #153D2E;
        --random-10: #6100FF;
        --random-11: #5700E3;
        --random-12: #30007D;

        //Typography
        --heading-1-700: normal normal 700 44px ' ', sans-serif;
        --heading-2-600: normal normal 600 36px 'Lexend', sans-serif;
        --heading-3-600: normal normal 600 32px 'Lexend', sans-serif;
        --heading-3-500: normal normal 500 32px 'Lexend', sans-serif;
        --heading-4-600: normal normal 600 28px 'Lexend', sans-serif;
        --heading-4-500: normal normal 500 28px 'Lexend', sans-serif;
        --heading-5-600: normal normal 600 24px 'Lexend', sans-serif;
        --heading-5-500: normal normal 500 24px 'Lexend', sans-serif;
        --heading-6-600: normal normal 600 20px 'Lexend', sans-serif;
        --heading-6-500: normal normal 500 20px 'Lexend', sans-serif;
        --heading-7-600: normal normal 600 16px 'Lexend', sans-serif;
        --heading-7-500: normal normal 500 16px 'Lexend', sans-serif;
        --body-1-400: normal normal 400 16px 'Inter', sans-serif;
        --body-1-600: normal normal 600 16px 'Inter', sans-serif;
        --body-2-400: normal normal 400 14px 'Inter', sans-serif;
        --body-2-500: normal normal 500 14px 'Inter', sans-serif;
        --button-big-text: normal normal 600 16px 'Inter', sans-serif;
        --button-medium-text: normal normal 600 14px 'Inter', sans-serif;
        --input-placeholder:normal normal 400 16px 'Inter', sans-serif;
        --input-label:normal normal 500 14px 'Inter', sans-serif;
    }

    * {

        margin: 0;
        padding: 0;
        box-sizing: border-box;

    }
    
    body { 

        font-family: 'Lexend', sans-serif;
        
    }   

`
