import styled from "styled-components";

export const TechBox = styled.div`
  @media (max-width: 700px) {
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: center;
    gap: 15px;

    margin: auto;
    padding: 25px;
    width: 780px;
    height: auto;
    
    border-bottom: 0.5px solid var(--grey-1);
    background-color: var(--grey-3);
    
    transition: 0.2s;
    h4 {
      color: var(--grey-0);
      margin-left: 0%;
    }
    
  }
  @media (min-width: 700px) {
    display: flex;
    flex-flow: column;
    justify-content: space-between;
    align-items: center;
    gap: 15px;

    margin: auto;
    padding: 25px;
    width: 780px;
    height: auto;
    
    border-bottom: 0.5px solid var(--grey-1);
    background-color: var(--grey-3);
    
    transition: 0.2s;

    h4 {
      font: var(--heading-2-600);
      color: var(--grey-0);
      margin-left: 10%;
    }
  }
`;

export const Logo = styled.img`
  // width:140px;
  height: 26px;
  cursor: pointer;
  border-radius: 100%;
`;
