import styled from "styled-components";


export const HeaderBox = styled.div`
    display:flex;
    justify-content:space-between;
    align-items:center;
    background-color:var(--grey-10);
    width:100%;
    height:80px;
    padding:25px;
    border-bottom:2px solid var(--grey-6);

    transition: 0.2s;

    @media (max-width: 699px) {

        &.active {

            padding: 2rem 1rem 18rem 1rem;

        }

    }
    h1 {
       font: var(--heading-1-700)
    }
    h2 {
        font: var(--heading-2-600)
    }
    h3 {
        font: var(--heading-3-600)
    }
    h4 {
        font: var(--headline-1-400)
    }
    h5 {
        font: var(--headline-2-600)
    }
` 

export const BoxNav =  styled.nav`
    display:flex;
    align-items:center;
    gap: 2.75rem;
    
`

export const Logo = styled.img `
    width:140px;
    height:26px;
    cursor: pointer;
`
