import * as yup from "yup";
import React from "react";
import { Input } from "../../components/input/input";
import { Button } from "../../components/button/button";
import { yupResolver } from "@hookform/resolvers/yup";
import { useForm, Resolver } from "react-hook-form";
import { RegisterFormValues } from "../../interface"

// const resolver: Resolver<RegisterFormValues > = async (values) => {
//   return {
//     values: values,
//     errors: !values.name
//       ? {
//           name: {
//             type: "required",
//             message: "This is required.",
//           },
//         }
//       : {},
//   };
// };

const SignupSchema = yup.object().shape({
    name: yup.string().required(),
    email: yup.string(),
    age: yup.number().required().positive().integer(),
    website: yup.string().url()
  });

export const RegisterForm = () => {
  const { register, handleSubmit, formState: { errors } } = useForm<RegisterFormValues>({
    resolver: yupResolver(SignupSchema)
  });
  const onSubmit = handleSubmit((data) => console.log(data));


  return (
    <form onSubmit={onSubmit}>
      <h2>Registro</h2>
      <Input
        label="nome"
        type="text"
        placeholder="pedrinho"
        errors="errinho"
        {...register("name")}
      ></Input>
      <Input
        label="email"
        type="text"
        placeholder="pedrinho@gmail"
        errors="errinho"
        {...register("email")}
      ></Input>
      <Input
        label="senha"
        type="text"
        placeholder="pedrinho123"
        errors="errinho"
        {...register("password")}
      ></Input>
      <Input
        label="confirmar senha"
        type="text"
        placeholder="pedrinho123"
        errors="errinho"
        {...register("confirmPassword")}
      ></Input>
      <Input
        label="bio"
        type="text"
        placeholder="javascript is good, java is evil."
        errors="errinho"
        {...register("bio")}
      ></Input>
      <Input
        label="contato"
        type="text"
        placeholder="12345678"
        errors="errinho"
        {...register("contact")}
      ></Input>
      <Input
        label="status"
        type="text"
        placeholder="juior dev, pleno, senior"
        errors="errinho"
        {...register("status")}
      ></Input>
      <Button
        type="submit"
        variant="default"
        variant_hover="default_hover"
        size="large"
      >
        {"Cadastrar"}
      </Button>
    </form>
  );
};
