import styled from "styled-components";

export const TechForm = styled.form`
  @media (max-width: 780px) {
  }
  position: absolute;
  margin: 160px auto;
  width: 370px;
  min-height: 350px;
  input,
  label,
  small,
  button {
    width: 90%;
    margin: 8px 5%;
  }
`;
export const FormCloseSection = styled.div`
  width: 100%;
  height: 80px;
  margin:-25px 0px;
  background-color: var(--grey-2);
`;
