import styled from "styled-components";
// import { width } from "styled-system";

const cardVariants = {
  default: { backgroundColor: "var(--grey-4)", color: "var(--grey-0)" },
  default_hover: { backgroundColor: "var(--grey-2)", color: "var(--grey-0)" },
};

const buttonSizes = {
  lesser: { width: "25px", height: "25px" },
  small: { width: "119px", height: "38px" },
  medium: { width: "133px", height: "43px" },
  large: { width: "146px", height: "48px" },
};
interface CardProps {
  variant: keyof typeof cardVariants;
  variant_hover: keyof typeof cardVariants;
  size: keyof typeof buttonSizes;
}

export const Card = styled.div<CardProps>`
  
    background-color:var(--grey-4);
    color:var(--grey-0);
    width: 752px;
    height: 50px; 
    &:hover{
        background-color:var(--grey-2);
    },         
    font:var(--button-medium-text);
    border: none;
    border-radius: 4px;
`;
