import styled from "styled-components";

export const LoginBox = styled.div`
  display: flex;
  flex-direction: column;

  width: 370px;
  height: 560px;

  border-radius: 4px;

  margin: auto auto;

  h1 {
    color: var(--primary-color);
    font: var(--heading-1-700);
    margin: 75px auto 
  }

  form {
    background-color: var(--grey-3);
    height: 500px;
  }

  input,
  label,
  small,
  button {
    width: 90%;
    margin: 8px 5%;
  }
`;
