import styled from "styled-components";

export const LoginBox = styled.div`

    display: flex;
    flex-direction: column;

    width: 370px;
    height: 560px;
    
    border-radius:4px;

    margin: auto auto;

    background-color: var(--grey-3);
    
    input, label, small{
        width: 90%;
    }
    
`;
