import styled from "styled-components";

export const HeaderBox = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: var(--grey-10);
  height: 80px;
  padding: 25px;
  border-bottom: 2px solid var(--grey-6);
  box-shadow: 0px 0.5px var(--grey-1);
  transition: 0.2s;

  @media (max-width: 699px) {
    &.active {
      padding: 2rem 1rem 18rem 1rem;
    }
  }
`;

export const BoxNav = styled.nav`
  top: 0px;
  position: fidex;
  display: flex;
  align-items: center;
  gap: 2.75rem;
  box-shadow: 0px 0.5px var(--grey-1);
`;

export const Logo = styled.img`
  // width:140px;
  height: 26px;
  cursor: pointer;
  border-radius: 100%;
`;
